import React, { useState } from 'react';

const App = () => {

    const [number, setNumber] = useState(0);

    const increm = () => {
        setNumber(number+1);
    }

    const decrem = () => {
        if(number == 0){
            alert("Reached the 0 limit");
        }else {
            setNumber(number-1);
        }
    }
    
    return (
        <>
            <div className='main_div'>
                <h1>{number}</h1>
                <button onClick={increm}>Increm</button>
                <button onClick={decrem}>Decrem</button>
            </div>
        </>
    );

}

export default App;
