import React, { useState } from 'react';
import AddIcon from '@material-ui/icons/Add';
import RemoveIcon from '@material-ui/icons/Remove';
import Button from '@material-ui/core/Button';

const App = () => {

    const [number, setNumber] = useState(0);

    const increm = () => {
        setNumber(number + 1);
    }

    const decrem = () => {
        if (number == 0) {
            alert("Reached the 0 limit");
        } else {
            setNumber(number - 1);
        }
    }

    return (
        <>
            <div className='main_div'>
                <div className='center_div'>
                    <h1>{number}</h1>
                    <div className='btn_div'>
                    <Tooltip title="Add">
                    <Button onClick={increm} className='button_green'>
                            <AddIcon />
                        </Button>
                    </Tooltip>
                        
                        <Button onClick={decrem} className='button_red'>
                            <RemoveIcon />
                        </Button>
                    </div>
                </div>
            </div>
        </>
    );

}

export default App;
